import { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Skeleton } from "@/components/ui/skeleton";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQuery } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useAiModalStore } from "@/stores/ai-modal-store";
import { 
  Lightbulb, 
  Edit3, 
  FileText, 
  Zap,
  Crown,
  Infinity
} from "lucide-react";

interface AiAssistantModalProps {
  open: boolean;
  onClose: () => void;
  projects: any[];
}

type PersonaType = "muse" | "editor" | "coach";

export default function AiAssistantModal({ open, onClose, projects }: AiAssistantModalProps) {
  const { toast } = useToast();
  const { prefillData, clearPrefill } = useAiModalStore();
  const [selectedPersona, setSelectedPersona] = useState<PersonaType | null>(null);
  const [selectedProject, setSelectedProject] = useState("");
  const [prompt, setPrompt] = useState("");
  const [creativity, setCreativity] = useState([70]);
  const [length, setLength] = useState("medium");
  const [result, setResult] = useState<string | null>(null);

  useEffect(() => {
    if (open && prefillData) {
      if (prefillData.persona) {
        setSelectedPersona(prefillData.persona);
      }
      setPrompt(prefillData.prompt);
      if (prefillData.projectId) {
        setSelectedProject(prefillData.projectId);
      }
      clearPrefill();
    }
  }, [open, prefillData, clearPrefill]);

  // Fetch AI usage data
  const { data: usageData, isLoading: usageLoading } = useQuery<{
    used: number;
    limit: number;
    remaining: number;
    tokens_used: number;
    resetDate: string;
    plan: string;
  }>({
    queryKey: ['/api/ai/usage'],
    enabled: open,
  });

  const personas = [
    {
      id: "muse" as PersonaType,
      name: "Muse",
      subtitle: "Creative Inspiration",
      description: "Generate evocative scenes with rich sensory details and emotional depth",
      icon: <Lightbulb className="h-5 w-5 text-chart-1" />,
      bgColor: "bg-chart-1/10",
      textColor: "text-chart-1",
      borderColor: "border-chart-1"
    },
    {
      id: "editor" as PersonaType,
      name: "Editor",
      subtitle: "Polish & Refine",
      description: "Improve clarity, grammar, and flow while preserving your voice",
      icon: <Edit3 className="h-5 w-5 text-accent" />,
      bgColor: "bg-accent/10",
      textColor: "text-accent",
      borderColor: "border-accent"
    },
    {
      id: "coach" as PersonaType,
      name: "Coach",
      subtitle: "Structure & Planning",
      description: "Create outlines, story beats, and structural guidance",
      icon: <FileText className="h-5 w-5 text-chart-2" />,
      bgColor: "bg-chart-2/10",
      textColor: "text-chart-2",
      borderColor: "border-chart-2"
    }
  ];

  const generateMutation = useMutation({
    mutationFn: async (data: { persona: PersonaType; projectId: string; userPrompt: string }) => {
      const response = await apiRequest("POST", "/api/ai/generate", {
        intent: data.persona === "muse" ? "draft_scene" : data.persona === "editor" ? "edit_paragraph" : "generate_outline",
        persona: data.persona,
        project_id: data.projectId,
        params: {
          max_tokens: length === "short" ? 400 : length === "medium" ? 800 : 1200,
          deterministic: creativity[0] < 30
        },
        userPrompt: data.userPrompt
      });
      return response.json();
    },
    onSuccess: (data) => {
      setResult(typeof data.content === "string" ? data.content : JSON.stringify(data.content, null, 2));
      queryClient.invalidateQueries({ queryKey: ['/api/ai/usage'] });
      toast({
        title: "Content Generated",
        description: "Your AI assistant has created new content!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Generation Failed",
        description: "Failed to generate content. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleGenerate = () => {
    if (!selectedPersona || !prompt.trim()) {
      toast({
        title: "Missing Information",
        description: "Please select a persona and enter a prompt.",
        variant: "destructive",
      });
      return;
    }

    generateMutation.mutate({
      persona: selectedPersona,
      projectId: selectedProject && selectedProject !== "none" ? selectedProject : "",
      userPrompt: prompt
    });
  };

  const handleClose = () => {
    setSelectedPersona(null);
    setSelectedProject("");
    setPrompt("");
    setCreativity([70]);
    setLength("medium");
    setResult(null);
    onClose();
  };

  const selectedPersonaData = personas.find(p => p.id === selectedPersona);

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-4xl max-h-[90vh] overflow-hidden" data-testid="modal-ai-assistant">
        <DialogHeader>
          <div className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-chart-1/10 rounded-lg flex items-center justify-center">
              <Zap className="h-5 w-5 text-chart-1" />
            </div>
            <div>
              <DialogTitle>AI Writing Assistant</DialogTitle>
              <DialogDescription>Choose your writing companion</DialogDescription>
            </div>
          </div>
        </DialogHeader>

        <div className="overflow-y-auto">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">

            {/* Left: Persona Selection */}
            <div className="space-y-4">
              <h4 className="font-medium text-card-foreground">Select AI Persona</h4>

              <div className="space-y-3">
                {personas.map((persona) => (
                  <Card 
                    key={persona.id}
                    className={`cursor-pointer transition-all hover:shadow-md ${
                      selectedPersona === persona.id 
                        ? `${persona.borderColor} ring-2 ring-opacity-20` 
                        : "border-border"
                    }`}
                    onClick={() => setSelectedPersona(persona.id)}
                    data-testid={`persona-${persona.id}`}
                  >
                    <CardContent className="p-4">
                      <div className="flex items-center space-x-3 mb-2">
                        <div className={`w-8 h-8 ${persona.bgColor} rounded-lg flex items-center justify-center`}>
                          {persona.icon}
                        </div>
                        <span className="font-medium text-card-foreground">
                          {persona.name} - {persona.subtitle}
                        </span>
                      </div>
                      <p className="text-sm text-muted-foreground ml-11">
                        {persona.description}
                      </p>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </div>

            {/* Right: Input Area */}
            <div className="space-y-4">
              <h4 className="font-medium text-card-foreground">Your Request</h4>

              <div className="space-y-4">
                {projects.length > 0 && (
                  <div>
                    <Label className="text-sm font-medium text-card-foreground mb-2">Project Context</Label>
                    <Select value={selectedProject} onValueChange={setSelectedProject}>
                      <SelectTrigger data-testid="select-ai-project">
                        <SelectValue placeholder="Select a project (optional)" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="none">No specific project</SelectItem>
                        {projects.map((project) => (
                          <SelectItem key={project.id} value={project.id}>
                            {project.title}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                )}

                <div>
                  <Label className="text-sm font-medium text-card-foreground mb-2">
                    What would you like help with?
                  </Label>
                  <Textarea 
                    value={prompt}
                    onChange={(e) => setPrompt(e.target.value)}
                    className="resize-none editor-content"
                    placeholder="Describe what you'd like the AI to help you with. Be specific about the scene, characters, or writing goals..."
                    data-testid="input-ai-prompt"
                  />
                </div>

                {/* AI Parameters */}
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label className="text-sm font-medium text-card-foreground mb-2">Creativity</Label>
                    <Slider
                      value={creativity}
                      onValueChange={setCreativity}
                      max={100}
                      step={1}
                      className="w-full"
                      data-testid="slider-creativity"
                    />
                    <div className="flex justify-between text-xs text-muted-foreground mt-1">
                      <span>Focused</span>
                      <span>Creative</span>
                    </div>
                  </div>
                  <div>
                    <Label className="text-sm font-medium text-card-foreground mb-2">Length</Label>
                    <Select value={length} onValueChange={setLength}>
                      <SelectTrigger data-testid="select-ai-length">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="short">Short (200-400 words)</SelectItem>
                        <SelectItem value="medium">Medium (400-800 words)</SelectItem>
                        <SelectItem value="long">Long (800+ words)</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                {/* Result Display */}
                {result && (
                  <div className="space-y-2">
                    <Label className="text-sm font-medium text-card-foreground">Generated Content</Label>
                    <div className="bg-muted/30 rounded-lg p-4 max-h-40 overflow-y-auto">
                      <pre className="text-sm whitespace-pre-wrap text-card-foreground">{result}</pre>
                    </div>
                  </div>
                )}
              </div>

              <div className="flex items-center justify-between pt-4 border-t border-border">
                <div className="flex items-center space-x-2">
                  {usageLoading ? (
                    <Skeleton className="h-5 w-40" />
                  ) : usageData ? (
                    <>
                      {usageData.limit === -1 ? (
                        <>
                          <Infinity className="h-4 w-4 text-chart-1" />
                          <div className="text-sm text-muted-foreground">
                            <span className="font-medium text-chart-1">Unlimited</span> AI generations
                          </div>
                        </>
                      ) : (
                        <>
                          <Crown className="h-4 w-4 text-chart-1" />
                          <div className="text-sm text-muted-foreground" data-testid="text-ai-usage">
                            <span className="font-medium text-chart-1">{usageData.remaining}</span> / {usageData.limit} AI sessions remaining
                          </div>
                        </>
                      )}
                    </>
                  ) : null}
                </div>
                <div className="flex space-x-3">
                  <Button 
                    variant="outline" 
                    onClick={handleClose}
                    disabled={generateMutation.isPending}
                    data-testid="button-ai-cancel"
                  >
                    Cancel
                  </Button>
                  <Button 
                    onClick={handleGenerate}
                    disabled={
                      generateMutation.isPending || 
                      !selectedPersona || 
                      !prompt.trim() || 
                      (usageData && usageData.limit !== -1 && usageData.remaining <= 0)
                    }
                    data-testid="button-ai-generate"
                  >
                    {generateMutation.isPending ? (
                      <>Generating...</>
                    ) : (
                      <>
                        <Zap className="mr-2 h-4 w-4" />
                        Generate Content
                      </>
                    )}
                  </Button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/project-modal.tsx.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { CalendarIcon } from "lucide-react";
import { format } from "date-fns";
import { cn } from "@/lib/utils";

interface ProjectModalProps {
  open: boolean;
  onClose: () => void;
  onSubmit: (data: any) => void;
  loading?: boolean;
}

export default function ProjectModal({ open, onClose, onSubmit, loading = false }: ProjectModalProps) {
  const [formData, setFormData] = useState({
    template: "blank",
    title: "",
    description: "",
    genre: "",
    targetWordCount: "",
    deadline: undefined as Date | undefined,
  });

  const genres = [
    "Fantasy",
    "Science Fiction",
    "Romance",
    "Mystery",
    "Literary Fiction",
    "Historical Fiction",
    "Thriller",
    "Horror",
    "Young Adult",
    "Other"
  ];

  const templates = [
    { value: "blank", label: "Blank Project", description: "Start with an empty project" },
    { value: "novel", label: "Novel Template", description: "3 chapters, 2 characters, worldbuilding & timeline" },
    { value: "screenplay", label: "Screenplay Template", description: "3 acts, 2 characters, locations & timeline" },
    { value: "short-story", label: "Short Story Template", description: "1 draft, character, setting & key event" },
  ];

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const submitData = {
      template: formData.template,
      title: formData.title,
      description: formData.description || undefined,
      genre: formData.genre || undefined,
      targetWordCount: formData.targetWordCount ? parseInt(formData.targetWordCount) : undefined,
      deadline: formData.deadline?.toISOString() || undefined,
    };

    onSubmit(submitData);
  };

  const handleClose = () => {
    setFormData({
      template: "blank",
      title: "",
      description: "",
      genre: "",
      targetWordCount: "",
      deadline: undefined,
    });
    onClose();
  };

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-md" data-testid="modal-create-project">
        <DialogHeader>
          <DialogTitle>Create New Project</DialogTitle>
          <DialogDescription>
            Start a new writing project with your story details.
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="template">Template</Label>
            <Select value={formData.template} onValueChange={(value) => setFormData({ ...formData, template: value })}>
              <SelectTrigger data-testid="select-project-template">
                <SelectValue placeholder="Select a template" />
              </SelectTrigger>
              <SelectContent>
                {templates.map((template) => (
                  <SelectItem key={template.value} value={template.value} data-testid={`option-template-${template.value}`}>
                    <div className="flex flex-col">
                      <span className="font-medium">{template.label}</span>
                      <span className="text-xs text-muted-foreground">{template.description}</span>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <Label htmlFor="title">Project Title *</Label>
            <Input
              id="title"
              value={formData.title}
              onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              placeholder="Enter your project title"
              required
              data-testid="input-project-title"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="genre">Genre</Label>
            <Select value={formData.genre} onValueChange={(value) => setFormData({ ...formData, genre: value })}>
              <SelectTrigger data-testid="select-project-genre">
                <SelectValue placeholder="Select a genre" />
              </SelectTrigger>
              <SelectContent>
                {genres.map((genre) => (
                  <SelectItem key={genre} value={genre}>
                    {genre}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <Label htmlFor="description">Description</Label>
            <Textarea
              id="description"
              value={formData.description}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              placeholder="Brief description of your project..."
              className="resize-none h-20"
              data-testid="input-project-description"
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="targetWordCount">Target Word Count</Label>
              <Input
                id="targetWordCount"
                type="number"
                value={formData.targetWordCount}
                onChange={(e) => setFormData({ ...formData, targetWordCount: e.target.value })}
                placeholder="50000"
                data-testid="input-target-word-count"
              />
            </div>
            <div className="space-y-2">
              <Label>Deadline</Label>
              <Popover>
                <PopoverTrigger asChild>
                  <Button
                    variant="outline"
                    className={cn(
                      "w-full justify-start text-left font-normal",
                      !formData.deadline && "text-muted-foreground"
                    )}
                    data-testid="button-select-deadline"
                  >
                    <CalendarIcon className="mr-2 h-4 w-4" />
                    {formData.deadline ? format(formData.deadline, "PPP") : "Pick a date"}
                  </Button>
                </PopoverTrigger>
                <PopoverContent className="w-auto p-0">
                  <Calendar
                    mode="single"
                    selected={formData.deadline}
                    onSelect={(date) => setFormData({ ...formData, deadline: date })}
                    initialFocus
                  />
                </PopoverContent>
              </Popover>
            </div>
          </div>

          <div className="flex items-center justify-end space-x-3 pt-4 border-t border-border">
            <Button 
              type="button" 
              variant="outline" 
              onClick={handleClose}
              disabled={loading}
              data-testid="button-cancel-project"
            >
              Cancel
            </Button>
            <Button 
              type="submit" 
              disabled={loading || !formData.title.trim()}
              data-testid="button-create-project"
            >
              {loading ? "Creating..." : "Create Project"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/rich-text-editor.t.content:
import { useEditor, EditorContent } from '@tiptap/react';
import StarterKit from '@tiptap/starter-kit';
import UnderlineExtension from '@tiptap/extension-underline';
import TextAlign from '@tiptap/extension-text-align';
import { Button } from "@/components/ui/button";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { useEffect, useMemo, useState, useRef } from 'react';
import * as Y from 'yjs';
import * as awarenessProtocol from 'y-protocols/awareness';
import { YjsCollaboration } from './yjs-collaboration-extension';
import { calculateWordCount } from '@shared/utils';
import { 
  Bold, 
  Italic, 
  Underline, 
  Strikethrough,
  List, 
  ListOrdered,
  Quote,
  Undo,
  Redo,
  Type,
  AlignLeft,
  AlignCenter,
  AlignRight,
  Users,
  Circle,
  Eye,
  Edit3,
  MessageSquare,
  Code,
  Code2,
  Minus,
  Check,
  Loader2
} from "lucide-react";

interface RichTextEditorProps {
  content: string;
  onChange: (content: string) => void;
  placeholder?: string;
  className?: string;
  ydoc?: Y.Doc | null;
  awareness?: awarenessProtocol.Awareness | null;
  xmlFragment?: Y.XmlFragment | null;
  isCollaborative?: boolean;
  readOnly?: boolean;
  onlineUsers?: Map<number, any>;
  userColor?: string;
  userRole?: 'owner' | 'editor' | 'reviewer' | 'reader' | null;
  userName?: string;
  onCommentClick?: () => void;
}


export default function RichTextEditor({ 
  content, 
  onChange, 
  placeholder = "Start writing...",
  className = "",
  ydoc,
  awareness,
  xmlFragment,
  isCollaborative = false,
  readOnly = false,
  onlineUsers,
  userColor,
  userRole,
  userName,
  onCommentClick
}: RichTextEditorProps) {

  // Autosave state management
  const [saveStatus, setSaveStatus] = useState<'saved' | 'typing' | 'saving'>('saved');
  const [lastSaved, setLastSaved] = useState<Date | null>(null);
  const saveTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const typingTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Word count state
  const [wordCount, setWordCount] = useState(0);

  // Configure extensions based on collaborative mode
  const extensions = useMemo(() => {
    const baseExtensions = [];

    if (isCollaborative && ydoc && awareness && xmlFragment) {
      // Use Yjs collaboration extension for proper sync
      baseExtensions.push(
        YjsCollaboration.configure({
          ydoc,
          awareness,
          xmlFragment: 'prosemirror',
          user: {
            name: userName || 'Anonymous',
            color: userColor || '#000000'
          }
        })
      );
      // Add StarterKit - Yjs extension handles history
      baseExtensions.push(StarterKit);
    } else {
      // Non-collaborative mode - use regular StarterKit with history
      baseExtensions.push(StarterKit);
    }

    // Add additional formatting extensions
    baseExtensions.push(
      UnderlineExtension,
      TextAlign.configure({
        types: ['heading', 'paragraph'],
      })
    );

    return baseExtensions;
  }, [isCollaborative, ydoc, awareness, xmlFragment, userName, userColor]);

  const editor = useEditor({
    extensions,
    content: isCollaborative ? undefined : content, // Don't set initial content in collaborative mode
    editable: !readOnly && userRole !== 'reader',
    onUpdate: ({ editor }) => {
      const htmlContent = editor.getHTML();

      // IMMEDIATELY save data - no debouncing for data persistence
      onChange(htmlContent);

      // Calculate and update word count immediately
      const newWordCount = calculateWordCount(htmlContent);
      setWordCount(newWordCount);

      // Set typing status immediately (UI feedback)
      setSaveStatus('typing');

      // Clear existing timeouts
      if (typingTimeoutRef.current) {
        clearTimeout(typingTimeoutRef.current);
      }
      if (saveTimeoutRef.current) {
        clearTimeout(saveTimeoutRef.current);
      }

      // After 500ms of no typing, change to saving (cosmetic only)
      typingTimeoutRef.current = setTimeout(() => {
        setSaveStatus('saving');

        // After another 300ms, mark as saved (cosmetic only)
        saveTimeoutRef.current = setTimeout(() => {
          setSaveStatus('saved');
          setLastSaved(new Date());
        }, 300);
      }, 500);
    },
    editorProps: {
      attributes: {
        class: 'prose prose-custom max-w-none focus:outline-none min-h-[200px] p-4',
        'data-testid': 'editor-content-area'
      },
    },
  });

  // Cleanup timeouts on unmount
  useEffect(() => {
    return () => {
      if (typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current);
      if (saveTimeoutRef.current) clearTimeout(saveTimeoutRef.current);
    };
  }, []);

  // Update content for non-collaborative mode
  useEffect(() => {
    if (!editor || isCollaborative) return;

    // Only update content if it's different and we're not in collaborative mode
    const currentContent = editor.getHTML();
    if (content !== currentContent) {
      editor.commands.setContent(content);
    }
  }, [content, editor, isCollaborative]);

  // Initialize word count when editor or content loads
  useEffect(() => {
    if (editor) {
      const htmlContent = editor.getHTML();
      const initialWordCount = calculateWordCount(htmlContent);
      setWordCount(initialWordCount);
    }
  }, [editor]);

  if (!editor) {
    return null;
  }

  // Calculate number of active users for display
  const activeUserCount = onlineUsers ? onlineUsers.size : 0;

  return (
    <div className={`border border-border rounded-lg ${className}`} data-testid="rich-text-editor">
      {/* Status Bar */}
      {isCollaborative && (
        <div className="bg-muted/50 px-3 py-2 flex items-center justify-between text-sm border-b border-border">
          <div className="flex items-center space-x-3">
            {/* Connection Status */}
            <div className="flex items-center space-x-1.5">
              <Circle className={`h-2 w-2 fill-current ${isCollaborative ? 'text-green-500' : 'text-gray-400'}`} />
              <span className="text-muted-foreground">
                {isCollaborative ? 'Connected' : 'Offline'}
              </span>
            </div>

            {/* Active Users */}
            {activeUserCount > 0 && (
              <div className="flex items-center space-x-1.5" data-testid="active-users-count">
                <Users className="h-4 w-4 text-muted-foreground" />
                <span className="text-muted-foreground">{activeUserCount} online</span>
              </div>
            )}

            {/* User Role */}
            {userRole && (
              <Badge variant="secondary" className="capitalize">
                {userRole === 'owner' && <Edit3 className="h-3 w-3 mr-1" />}
                {userRole === 'reader' && <Eye className="h-3 w-3 mr-1" />}
                {userRole}
              </Badge>
            )}
          </div>

          {/* Comment Button */}
          {onCommentClick && userRole !== 'reader' && (
            <Button
              variant="ghost"
              size="sm"
              onClick={onCommentClick}
              data-testid="button-add-comment"
            >
              <MessageSquare className="h-4 w-4 mr-1" />
              Comment
            </Button>
          )}
        </div>
      )}

      {/* Toolbar */}
      <div className="border-b border-border p-2 flex items-center justify-between flex-wrap gap-1">
        <div className="flex items-center space-x-1 flex-wrap">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleBold().run()}
            className={editor.isActive('bold') ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-bold"
          >
            <Bold className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleItalic().run()}
            className={editor.isActive('italic') ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-italic"
          >
            <Italic className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleUnderline().run()}
            className={editor.isActive('underline') ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-underline"
          >
            <Underline className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleStrike().run()}
            className={editor.isActive('strike') ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-strikethrough"
          >
            <Strikethrough className="h-4 w-4" />
          </Button>

          <Separator orientation="vertical" className="h-6" />

          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleHeading({ level: 1 }).run()}
            className={editor.isActive('heading', { level: 1 }) ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-h1"
          >
            H1
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleHeading({ level: 2 }).run()}
            className={editor.isActive('heading', { level: 2 }) ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-h2"
          >
            H2
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleHeading({ level: 3 }).run()}
            className={editor.isActive('heading', { level: 3 }) ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-h3"
          >
            H3
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().setParagraph().run()}
            className={editor.isActive('paragraph') ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-paragraph"
          >
            <Type className="h-4 w-4" />
          </Button>

          <Separator orientation="vertical" className="h-6" />

          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleCode().run()}
            className={editor.isActive('code') ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-code"
          >
            <Code className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleCodeBlock().run()}
            className={editor.isActive('codeBlock') ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-code-block"
          >
            <Code2 className="h-4 w-4" />
          </Button>

          <Separator orientation="vertical" className="h-6" />

          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().setTextAlign('left').run()}
            className={editor.isActive({ textAlign: 'left' }) ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-align-left"
          >
            <AlignLeft className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().setTextAlign('center').run()}
            className={editor.isActive({ textAlign: 'center' }) ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-align-center"
          >
            <AlignCenter className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().setTextAlign('right').run()}
            className={editor.isActive({ textAlign: 'right' }) ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-align-right"
          >
            <AlignRight className="h-4 w-4" />
          </Button>

          <Separator orientation="vertical" className="h-6" />

          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleBulletList().run()}
            className={editor.isActive('bulletList') ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-bullet-list"
          >
            <List className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleOrderedList().run()}
            className={editor.isActive('orderedList') ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-ordered-list"
          >
            <ListOrdered className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().toggleBlockquote().run()}
            className={editor.isActive('blockquote') ? 'bg-muted' : ''}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-quote"
          >
            <Quote className="h-4 w-4" />
          </Button>

          <Separator orientation="vertical" className="h-6" />

          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().setHorizontalRule().run()}
            disabled={readOnly || userRole === 'reader'}
            data-testid="button-horizontal-rule"
          >
            <Minus className="h-4 w-4" />
          </Button>

          <Separator orientation="vertical" className="h-6" />

          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().undo().run()}
            disabled={!editor.can().undo() || readOnly || userRole === 'reader'}
            data-testid="button-undo"
          >
            <Undo className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => editor.chain().focus().redo().run()}
            disabled={!editor.can().redo() || readOnly || userRole === 'reader'}
            data-testid="button-redo"
          >
            <Redo className="h-4 w-4" />
          </Button>
        </div>

        {/* Autosave Indicator and Word Count */}
        <div className="flex items-center space-x-3">
          {/* Word Count */}
          <div className="text-xs text-muted-foreground" data-testid="text-word-count">
            {wordCount.toLocaleString()} {wordCount === 1 ? 'word' : 'words'}
          </div>

          {/* Autosave Indicator */}
          {!isCollaborative && (
            <div className="flex items-center space-x-1.5 text-xs text-muted-foreground" data-testid="autosave-indicator">
              {saveStatus === 'typing' && (
                <>
                  <Loader2 className="h-3 w-3 animate-spin" />
                  <span>Typing...</span>
                </>
              )}
              {saveStatus === 'saving' && (
                <>
                  <Loader2 className="h-3 w-3 animate-spin" />
                  <span>Saving...</span>
                </>
              )}
              {saveStatus === 'saved' && lastSaved && (
                <>
                  <Check className="h-3 w-3 text-green-500" />
                  <span>Saved {lastSaved.toLocaleTimeString()}</span>
                </>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Editor Content with Live Cursors Overlay */}
      <div className="relative">
        {/* Live Cursors Overlay */}
        {isCollaborative && onlineUsers && onlineUsers.size > 0 && (
          <div className="absolute inset-0 pointer-events-none z-10" data-testid="live-cursors-overlay">
            {Array.from(onlineUsers.values()).map((userState, index) => {
              if (!userState?.user || !userState.cursor) return null;
              const cursor = userState.cursor;
              const cursorColor = userState.user.color || '#808080';

              return (
                <div
