import { useEffect, useState, useMemo } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest, queryClient } from "@/lib/queryClient";
import Sidebar from "@/components/sidebar";
import { 
  Clock, 
  Plus, 
  Edit, 
  Trash2, 
  Search,
  Calendar,
  MapPin,
  Users,
  GripVertical,
  ChevronDown,
  ChevronRight
} from "lucide-react";
import { Project, TimelineEvent } from "@shared/schema";
import {
  DndContext,
  DragEndEvent,
  DragStartEvent,
  DragOverlay,
  closestCenter,
  PointerSensor,
  KeyboardSensor,
  useSensor,
  useSensors,
} from '@dnd-kit/core';
import {
  SortableContext,
  sortableKeyboardCoordinates,
  verticalListSortingStrategy,
  useSortable,
} from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { cn } from "@/lib/utils";

const importanceLevels = [
  { value: 1, label: "Minor", color: "bg-muted" },
  { value: 2, label: "Notable", color: "bg-chart-3" },
  { value: 3, label: "Significant", color: "bg-chart-2" },
  { value: 4, label: "Major", color: "bg-chart-1" },
  { value: 5, label: "Critical", color: "bg-destructive" }
];

function SortableEventCard({ 
  event, 
  onEdit, 
  onDelete 
}: { 
  event: TimelineEvent; 
  onEdit: () => void; 
  onDelete: () => void; 
}) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: event.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.5 : 1,
  };

  const getImportanceInfo = (importance: number) => {
    return importanceLevels.find(level => level.value === importance) || importanceLevels[2];
  };

  const importanceInfo = getImportanceInfo(event.importance || 3);

  return (
    <div ref={setNodeRef} style={style} className="relative flex space-x-4" data-testid={`card-event-${event.id}`}>
      <button
        {...attributes}
        {...listeners}
        className="cursor-grab active:cursor-grabbing mt-1 text-muted-foreground hover:text-foreground z-10 flex-shrink-0"
        data-testid={`drag-handle-${event.id}`}
      >
        <GripVertical className="h-5 w-5" />
      </button>
      <div className={`w-12 h-12 ${importanceInfo.color} rounded-lg flex items-center justify-center z-10 flex-shrink-0`}>
        <Clock className="h-6 w-6 text-white" />
      </div>
      <div className="flex-1 min-w-0">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <div className="flex items-center space-x-2 mb-1">
              <h4 className="font-semibold text-card-foreground" data-testid={`text-event-title-${event.id}`}>
                {event.title}
              </h4>
              <Badge variant="outline" className={importanceInfo.color}>
                {importanceInfo.label}
              </Badge>
            </div>
            {event.description && (
              <p className="text-muted-foreground text-sm mb-2">
                {event.description}
              </p>
            )}

            <div className="flex flex-wrap items-center gap-4 text-sm text-muted-foreground">
              {event.relatedCharacters && event.relatedCharacters.length > 0 && (
                <div className="flex items-center space-x-1">
                  <Users className="h-3 w-3" />
                  <span>{event.relatedCharacters.join(', ')}</span>
                </div>
              )}
              {event.relatedLocations && event.relatedLocations.length > 0 && (
                <div className="flex items-center space-x-1">
                  <MapPin className="h-3 w-3" />
                  <span>{event.relatedLocations.join(', ')}</span>
                </div>
              )}
            </div>

            {event.tags && event.tags.length > 0 && (
              <div className="flex flex-wrap gap-1 mt-2">
                {event.tags.map((tag, tagIndex) => (
                  <Badge key={tagIndex} variant="secondary" className="text-xs">
                    {tag}
                  </Badge>
                ))}
              </div>
            )}
          </div>

          <div className="flex items-center space-x-1 ml-4">
            <Button
              variant="ghost"
              size="sm"
              onClick={onEdit}
              data-testid={`button-edit-event-${event.id}`}
            >
              <Edit className="h-4 w-4" />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={onDelete}
              data-testid={`button-delete-event-${event.id}`}
            >
              <Trash2 className="h-4 w-4 text-destructive" />
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default function Timeline() {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const queryClientInstance = useQueryClient();
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [showEventModal, setShowEventModal] = useState(false);
  const [editingEvent, setEditingEvent] = useState<TimelineEvent | null>(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [filterImportance, setFilterImportance] = useState("all");
  const [expandedYears, setExpandedYears] = useState<Set<string>>(new Set());
  const [activeId, setActiveId] = useState<string | null>(null);
  const [draggedEvent, setDraggedEvent] = useState<TimelineEvent | null>(null);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    date: "",
    importance: 3,
    tags: "",
    relatedCharacters: "",
    relatedLocations: ""
  });

  // Drag sensors
  const sensors = useSensors(
    useSensor(PointerSensor, {
      activationConstraint: {
        distance: 8,
      },
    }),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: projects = [] } = useQuery<Project[]>({
    queryKey: ['/api/projects'],
    enabled: isAuthenticated,
    retry: false,
  });

  const { data: events = [], isLoading: eventsLoading } = useQuery<TimelineEvent[]>({
    queryKey: ['/api/timeline'],
    enabled: isAuthenticated && projects.length > 0,
    queryFn: async () => {
      if (projects.length > 0) {
        const response = await fetch(`/api/projects/${projects[0].id}/timeline`, {
          credentials: "include",
        });
        if (!response.ok) throw new Error(`${response.status}: ${response.statusText}`);
        return response.json();
      }
      return [];
    },
    retry: false,
  });

  const createEventMutation = useMutation({
    mutationFn: async (eventData: any) => {
      if (projects.length === 0) throw new Error("No project available");
      await apiRequest("POST", `/api/projects/${projects[0].id}/timeline`, eventData);
    },
    onSuccess: () => {
      queryClientInstance.invalidateQueries({ queryKey: ['/api/timeline'] });
      setShowEventModal(false);
      setFormData({
        title: "",
        description: "",
        date: "",
        importance: 3,
        tags: "",
        relatedCharacters: "",
        relatedLocations: ""
      });
      toast({
        title: "Success",
        description: "Timeline event created successfully!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create event. Please try again.",
        variant: "destructive",
      });
    },
  });

  const updateEventMutation = useMutation({
    mutationFn: async (data: { id: string; updates: any }) => {
      await apiRequest("PUT", `/api/timeline/${data.id}`, data.updates);
    },
    onSuccess: () => {
      queryClientInstance.invalidateQueries({ queryKey: ['/api/timeline'] });
      setShowEventModal(false);
      setEditingEvent(null);
      toast({
        title: "Success",
        description: "Event updated successfully!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update event. Please try again.",
        variant: "destructive",
      });
    },
  });

  const deleteEventMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/timeline/${id}`);
    },
    onSuccess: () => {
      queryClientInstance.invalidateQueries({ queryKey: ['/api/timeline'] });
      toast({
        title: "Success",
        description: "Event deleted successfully!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to delete event. Please try again.",
        variant: "destructive",
      });
    },
  });

  const reorderEventMutation = useMutation({
    mutationFn: async (data: { 
      eventId: string; 
      targetId: string; 
      targetDate: string | null;
    }) => {
      const targetEvent = events.find(e => e.id === data.targetId);

      if (!targetEvent) return;

      const newOrderIndex = targetEvent.orderIndex ?? 0;
      const activeEvent = events.find(e => e.id === data.eventId);
      const dateChanged = activeEvent && activeEvent.date !== targetEvent.date;

      await apiRequest("PATCH", `/api/timeline/${data.eventId}/reorder`, {
        orderIndex: newOrderIndex,
        ...(dateChanged && { date: targetEvent.date })
      });
    },
    onMutate: async (data) => {
      await queryClientInstance.cancelQueries({ queryKey: ['/api/timeline'] });
      const previousEvents = queryClientInstance.getQueryData(['/api/timeline']);

      queryClientInstance.setQueryData(['/api/timeline'], (old: TimelineEvent[] | undefined) => {
        if (!old) return old;

        const oldIndex = old.findIndex(e => e.id === data.eventId);
        const newIndex = old.findIndex(e => e.id === data.targetId);

        if (oldIndex === -1 || newIndex === -1) return old;

        const newArray = [...old];
        const [movedEvent] = newArray.splice(oldIndex, 1);

        const targetEvent = old[newIndex];
        if (targetEvent && movedEvent.date !== targetEvent.date) {
          movedEvent.date = targetEvent.date;
        }

        newArray.splice(newIndex, 0, movedEvent);

        return newArray;
      });

      return { previousEvents };
    },
    onError: (err, variables, context) => {
      if (context?.previousEvents) {
        queryClientInstance.setQueryData(['/api/timeline'], context.previousEvents);
      }
      toast({
        title: "Error",
        description: "Failed to reorder events. Please try again.",
        variant: "destructive",
      });
    },
    onSuccess: () => {
      queryClientInstance.invalidateQueries({ queryKey: ['/api/timeline'] });
      toast({
        title: "Success",
        description: "Event reordered successfully!",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const submitData = {
      title: formData.title,
      description: formData.description || undefined,
      date: formData.date,
      importance: formData.importance,
      tags: formData.tags ? formData.tags.split(',').map(tag => tag.trim()).filter(Boolean) : undefined,
      relatedCharacters: formData.relatedCharacters ? formData.relatedCharacters.split(',').map(char => char.trim()).filter(Boolean) : undefined,
      relatedLocations: formData.relatedLocations ? formData.relatedLocations.split(',').map(loc => loc.trim()).filter(Boolean) : undefined
    };

    if (editingEvent) {
      updateEventMutation.mutate({
        id: editingEvent.id,
        updates: submitData
      });
    } else {
      createEventMutation.mutate(submitData);
    }
  };

  const handleEdit = (event: TimelineEvent) => {
    setEditingEvent(event);
    setFormData({
      title: event.title,
      description: event.description || "",
      date: event.date || "",
      importance: event.importance || 3,
      tags: event.tags?.join(', ') || "",
      relatedCharacters: event.relatedCharacters?.join(', ') || "",
      relatedLocations: event.relatedLocations?.join(', ') || ""
    });
    setShowEventModal(true);
  };

  const handleDelete = (event: TimelineEvent) => {
    if (confirm(`Are you sure you want to delete "${event.title}"?`)) {
      deleteEventMutation.mutate(event.id);
    }
  };

  const handleCloseModal = () => {
    setShowEventModal(false);
    setEditingEvent(null);
    setFormData({
      title: "",
      description: "",
      date: "",
      importance: 3,
      tags: "",
      relatedCharacters: "",
      relatedLocations: ""
    });
  };

  const handleDragStart = (event: DragStartEvent) => {
    const { active } = event;
    setActiveId(active.id as string);
    const draggedEvt = events.find(e => e.id === active.id);
    setDraggedEvent(draggedEvt || null);
  };

  const handleDragEnd = (event: DragEndEvent) => {
    const { active, over } = event;

    if (!over || active.id === over.id) {
      setActiveId(null);
      setDraggedEvent(null);
      return;
    }

    const activeEvent = events.find(e => e.id === active.id);
    const overEvent = events.find(e => e.id === over.id);

    if (!activeEvent || !overEvent) {
      setActiveId(null);
      setDraggedEvent(null);
      return;
    }

    reorderEventMutation.mutate({
      eventId: activeEvent.id,
      targetId: overEvent.id,
      targetDate: overEvent.date || null,
    });

    setActiveId(null);
    setDraggedEvent(null);
  };

  // Group events by year/era with sorting by orderIndex first, then importance
  const groupedEvents = useMemo(() => {
    const filtered = events.filter((event: TimelineEvent) => {
      const matchesSearch = event.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        event.description?.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesImportance = filterImportance === "all" || (event.importance || 3).toString() === filterImportance;
      return matchesSearch && matchesImportance;
    });

    const grouped = filtered.reduce((acc: { [key: string]: TimelineEvent[] }, event: TimelineEvent) => {
      const year = event.date || "Undated";
      if (!acc[year]) {
        acc[year] = [];
      }
      acc[year].push(event);
      return acc;
    }, {});

    // Sort each group by orderIndex first, then importance
    Object.keys(grouped).forEach(year => {
      grouped[year].sort((a, b) => {
        const aOrder = a.orderIndex ?? 0;
        const bOrder = b.orderIndex ?? 0;
        if (aOrder !== bOrder) return aOrder - bOrder;
        return (b.importance ?? 3) - (a.importance ?? 3);
      });
    });

    return grouped;
  }, [events, searchTerm, filterImportance]);

  // Sort years/eras
  const sortedYears = useMemo(() => {
    return Object.keys(groupedEvents).sort((a, b) => {
      if (a === "Undated") return 1;
      if (b === "Undated") return -1;
      return a.localeCompare(b);
    });
  }, [groupedEvents]);

  const toggleYear = (year: string) => {
    const newExpanded = new Set(expandedYears);
    if (newExpanded.has(year)) {
      newExpanded.delete(year);
    } else {
      newExpanded.add(year);
    }
    setExpandedYears(newExpanded);
  };

  if (!isAuthenticated || isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="flex h-screen overflow-hidden bg-background">
      <Sidebar 
        collapsed={sidebarCollapsed} 
        onToggleCollapse={() => setSidebarCollapsed(!sidebarCollapsed)}
        currentPath="/timeline"
      />

      <main className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <h1 className="text-2xl font-semibold text-card-foreground" data-testid="text-timeline-title">
                Timeline
              </h1>
              <Badge variant="secondary" data-testid="badge-event-count">
                {events.length} events
              </Badge>
            </div>

            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <Select value={filterImportance} onValueChange={setFilterImportance}>
                  <SelectTrigger className="w-40" data-testid="select-filter-importance">
                    <SelectValue placeholder="Filter by importance" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Levels</SelectItem>
                    {importanceLevels.map((level) => (
                      <SelectItem key={level.value} value={level.value.toString()}>
                        {level.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>

                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                  <Input
                    placeholder="Search events..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10 w-64"
                    data-testid="input-search-events"
                  />
                </div>
              </div>

              <Button 
                onClick={() => setShowEventModal(true)}
                data-testid="button-add-event"
              >
                <Plus className="mr-2 h-4 w-4" />
                Add Event
              </Button>
            </div>
          </div>
        </header>

        {/* Timeline Content */}
        <div className="flex-1 overflow-auto p-6">
          {eventsLoading ? (
            <div className="space-y-6">
              {[1, 2, 3].map(i => (
                <Card key={i} className="animate-pulse">
                  <CardContent className="p-6">
                    <div className="h-4 bg-muted rounded w-1/4 mb-4"></div>
                    <div className="space-y-4">
                      {[1, 2].map(j => (
                        <div key={j} className="flex space-x-4">
                          <div className="w-12 h-12 bg-muted rounded-lg"></div>
                          <div className="flex-1">
                            <div className="h-4 bg-muted rounded w-3/4 mb-2"></div>
                            <div className="h-3 bg-muted rounded w-1/2"></div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : Object.keys(groupedEvents).length === 0 ? (
            <div className="text-center py-16" data-testid="empty-events-state">
              <Clock className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-medium text-card-foreground mb-2">
                {searchTerm || filterImportance !== "all" ? "No events found" : "No timeline events yet"}
              </h3>
              <p className="text-muted-foreground mb-4">
                {searchTerm || filterImportance !== "all"
                  ? "Try adjusting your search or filter."
                  : "Start building your story's chronology with important events."
                }
              </p>
              {!searchTerm && filterImportance === "all" && (
                <Button 
                  onClick={() => setShowEventModal(true)}
                  data-testid="button-create-first-event"
                >
                  <Plus className="mr-2 h-4 w-4" />
                  Create Your First Event
                </Button>
              )}
            </div>
          ) : (
            <DndContext
              sensors={sensors}
              collisionDetection={closestCenter}
              onDragStart={handleDragStart}
              onDragEnd={handleDragEnd}
            >
              <div className="space-y-6">
                {sortedYears.map((year) => {
                  const yearEvents = groupedEvents[year];
                  const isExpanded = expandedYears.has(year);

                  return (
                    <Card key={year} data-testid={`card-year-${year}`}>
                      <CardHeader 
                        className="cursor-pointer hover:bg-muted/30 transition-colors"
                        onClick={() => toggleYear(year)}
                      >
                        <div className="flex items-center justify-between">
                          <div className="flex items-center space-x-3">
                            {isExpanded ? (
                              <ChevronDown className="h-5 w-5 text-muted-foreground" />
                            ) : (
                              <ChevronRight className="h-5 w-5 text-muted-foreground" />
                            )}
                            <div className="flex items-center space-x-2">
                              <Calendar className="h-5 w-5 text-accent" />
                              <CardTitle className="text-xl">{year}</CardTitle>
                            </div>
                          </div>
                          <Badge variant="outline">
                            {yearEvents.length} event{yearEvents.length !== 1 ? 's' : ''}
                          </Badge>
                        </div>
                      </CardHeader>

                      {isExpanded && (
                        <CardContent className="space-y-4">
                          <div className="relative">
                            <div className="absolute left-6 top-0 bottom-0 w-px bg-border"></div>
                            <SortableContext
                              items={yearEvents.map(e => e.id)}
                              strategy={verticalListSortingStrategy}
                            >
                              <div className="space-y-6">
                                {yearEvents.map((event) => (
                                  <SortableEventCard
                                    key={event.id}
                                    event={event}
                                    onEdit={() => handleEdit(event)}
                                    onDelete={() => handleDelete(event)}
                                  />
                                ))}
                              </div>
                            </SortableContext>
                          </div>
                        </CardContent>
                      )}
                    </Card>
                  );
                })}
              </div>
              <DragOverlay>
                {draggedEvent ? (
                  <Card className="shadow-2xl opacity-90">
                    <CardContent className="p-4">
                      <div className="flex items-center space-x-3">
                        <div className="w-10 h-10 bg-primary rounded-lg flex items-center justify-center">
                          <Clock className="h-5 w-5 text-white" />
                        </div>
                        <div>
                          <h4 className="font-semibold">{draggedEvent.title}</h4>
                          {draggedEvent.date && (
                            <p className="text-sm text-muted-foreground">{draggedEvent.date}</p>
                          )}
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ) : null}
              </DragOverlay>
            </DndContext>
          )}
        </div>
      </main>

      {/* Event Modal */}
      <Dialog open={showEventModal} onOpenChange={handleCloseModal}>
        <DialogContent className="sm:max-w-lg max-h-[90vh] overflow-y-auto" data-testid="modal-timeline-event">
          <DialogHeader>
            <DialogTitle>
              {editingEvent ? "Edit Timeline Event" : "Create New Timeline Event"}
            </DialogTitle>
            <DialogDescription>
              {editingEvent ? "Update your timeline event." : "Add a new event to your story's chronology."}
            </DialogDescription>
          </DialogHeader>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="title">Event Title *</Label>
              <Input
                id="title"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                placeholder="Enter event title"
                required
                data-testid="input-event-title"
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="date">Date/Era</Label>
                <Input
                  id="date"
                  value={formData.date}
                  onChange={(e) => setFormData({ ...formData, date: e.target.value })}
                  placeholder="e.g., 1485, Age of Dragons"
                  data-testid="input-event-date"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="importance">Importance *</Label>
                <Select value={formData.importance.toString()} onValueChange={(value) => setFormData({ ...formData, importance: parseInt(value) })}>
                  <SelectTrigger data-testid="select-event-importance">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {importanceLevels.map((level) => (
                      <SelectItem key={level.value} value={level.value.toString()}>
                        {level.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                placeholder="Describe what happens in this event"
                rows={3}
                data-testid="textarea-event-description"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="relatedCharacters">Related Characters</Label>
              <Input
                id="relatedCharacters"
                value={formData.relatedCharacters}
                onChange={(e) => setFormData({ ...formData, relatedCharacters: e.target.value })}
                placeholder="Character names separated by commas"
                data-testid="input-event-characters"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="relatedLocations">Related Locations</Label>
              <Input
                id="relatedLocations"
                value={formData.relatedLocations}
                onChange={(e) => setFormData({ ...formData, relatedLocations: e.target.value })}
                placeholder="Location names separated by commas"
                data-testid="input-event-locations"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="tags">Tags</Label>
              <Input
                id="tags"
                value={formData.tags}
                onChange={(e) => setFormData({ ...formData, tags: e.target.value })}
                placeholder="Tags separated by commas"
                data-testid="input-event-tags"
              />
            </div>

            <div className="flex justify-end space-x-2 pt-4">
              <Button 
                type="button" 
                variant="outline" 
                onClick={handleCloseModal}
                data-testid="button-cancel-event"
              >
                Cancel
              </Button>
              <Button 
                type="submit"
                disabled={createEventMutation.isPending || updateEventMutation.isPending}
                data-testid="button-save-event"
              >
                {createEventMutation.isPending || updateEventMutation.isPending ? "Saving..." : (editingEvent ? "Update Event" : "Create Event")}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/worldbuilding.tsx.content:
import { useEffect, useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import Sidebar from "@/components/sidebar";
import { 
  Globe, 
  Plus, 
  Edit, 
  Trash2, 
  Search,
  MapPin,
  Mountain,
  Building,
  Crown,
  Scroll,
  Sparkles
} from "lucide-react";
import { Project, WorldbuildingEntry, WorldbuildingDetails } from "@shared/schema";

const entryTypes = [
  { value: "location", label: "Location", icon: MapPin, color: "text-chart-1" },
  { value: "culture", label: "Culture", icon: Crown, color: "text-chart-2" },
  { value: "organization", label: "Organization", icon: Building, color: "text-accent" },
  { value: "magic_system", label: "Magic System", icon: Sparkles, color: "text-chart-5" },
  { value: "technology", label: "Technology", icon: Scroll, color: "text-chart-4" },
  { value: "history", label: "History", icon: Mountain, color: "text-destructive" },
  { value: "other", label: "Other", icon: Globe, color: "text-muted-foreground" }
];

export default function Worldbuilding() {
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [showEntryModal, setShowEntryModal] = useState(false);
  const [editingEntry, setEditingEntry] = useState<WorldbuildingEntry | null>(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [filterType, setFilterType] = useState("all");
  const [formData, setFormData] = useState({
    title: "",
    type: "",
    description: "",
    details: "",
    tags: ""
  });

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: projects = [] } = useQuery<Project[]>({
    queryKey: ['/api/projects'],
    enabled: isAuthenticated,
    retry: false,
  });

  const { data: entries = [], isLoading: entriesLoading } = useQuery<WorldbuildingEntry[]>({
    queryKey: ['/api/worldbuilding'],
    enabled: isAuthenticated && projects.length > 0,
    queryFn: async () => {
      if (projects.length > 0) {
        const response = await fetch(`/api/projects/${projects[0].id}/worldbuilding`, {
          credentials: "include",
        });
        if (!response.ok) throw new Error(`${response.status}: ${response.statusText}`);
        return response.json();
      }
      return [];
    },
    retry: false,
  });

  const createEntryMutation = useMutation({
    mutationFn: async (entryData: any) => {
      if (projects.length === 0) throw new Error("No project available");
      await apiRequest("POST", `/api/projects/${projects[0].id}/worldbuilding`, entryData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/worldbuilding'] });
      setShowEntryModal(false);
      setFormData({
        title: "",
        type: "",
        description: "",
        details: "",
        tags: ""
      });
      toast({
        title: "Success",
        description: "Worldbuilding entry created successfully!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to create entry. Please try again.",
        variant: "destructive",
      });
    },
  });

  const updateEntryMutation = useMutation({
    mutationFn: async (data: { id: string; updates: any }) => {
      await apiRequest("PUT", `/api/worldbuilding/${data.id}`, data.updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/worldbuilding'] });
      setShowEntryModal(false);
      setEditingEntry(null);
      toast({
        title: "Success",
        description: "Entry updated successfully!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update entry. Please try again.",
        variant: "destructive",
      });
    },
  });

  const deleteEntryMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/worldbuilding/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/worldbuilding'] });
      toast({
        title: "Success",
        description: "Entry deleted successfully!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to delete entry. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const submitData = {
      title: formData.title,
      type: formData.type,
      description: formData.description || undefined,
      details: formData.details ? { content: formData.details } : undefined,
      tags: formData.tags ? formData.tags.split(',').map(tag => tag.trim()).filter(Boolean) : undefined
    };

    if (editingEntry) {
      updateEntryMutation.mutate({
        id: editingEntry.id,
        updates: submitData
      });
    } else {
      createEntryMutation.mutate(submitData);
    }
  };

  const handleEdit = (entry: WorldbuildingEntry) => {
    setEditingEntry(entry);
    setFormData({
      title: entry.title,
      type: entry.type,
      description: entry.description || "",
      details: entry.details && typeof entry.details === 'object' && 'content' in entry.details && typeof entry.details.content === 'string' ? entry.details.content : "",
      tags: entry.tags?.join(', ') || ""
    });
    setShowEntryModal(true);
  };

  const handleDelete = (entry: WorldbuildingEntry) => {
    if (confirm(`Are you sure you want to delete "${entry.title}"?`)) {
      deleteEntryMutation.mutate(entry.id);
    }
  };

  const handleCloseModal = () => {
    setShowEntryModal(false);
    setEditingEntry(null);
    setFormData({
      title: "",
      type: "",
      description: "",
      details: "",
      tags: ""
    });
  };

  const filteredEntries = entries.filter((entry: WorldbuildingEntry) => {
    const matchesSearch = entry.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      entry.description?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = filterType === "all" || entry.type === filterType;
    return matchesSearch && matchesType;
  });

  const getTypeInfo = (type: string) => {
    return entryTypes.find(t => t.value === type) || entryTypes[entryTypes.length - 1];
  };

  if (!isAuthenticated || isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="flex h-screen overflow-hidden bg-background">
      <Sidebar 
        collapsed={sidebarCollapsed} 
        onToggleCollapse={() => setSidebarCollapsed(!sidebarCollapsed)}
        currentPath="/worldbuilding"
      />

      <main className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <h1 className="text-2xl font-semibold text-card-foreground" data-testid="text-worldbuilding-title">
                World Building
              </h1>
              <Badge variant="secondary" data-testid="badge-entry-count">
                {entries.length} entries
              </Badge>
            </div>

            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <Select value={filterType} onValueChange={setFilterType}>
                  <SelectTrigger className="w-40" data-testid="select-filter-type">
                    <SelectValue placeholder="Filter by type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Types</SelectItem>
                    {entryTypes.map((type) => (
                      <SelectItem key={type.value} value={type.value}>
                        {type.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>

                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                  <Input
                    placeholder="Search entries..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10 w-64"
                    data-testid="input-search-entries"
                  />
                </div>
              </div>

              <Button 
                onClick={() => setShowEntryModal(true)}
                data-testid="button-add-entry"
              >
                <Plus className="mr-2 h-4 w-4" />
                Add Entry
              </Button>
            </div>
          </div>
        </header>

        {/* Worldbuilding Content */}
        <div className="flex-1 overflow-auto p-6">
          {entriesLoading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {[1, 2, 3, 4, 5, 6].map(i => (
                <Card key={i} className="animate-pulse">
                  <CardContent className="p-6">
                    <div className="h-4 bg-muted rounded w-3/4 mb-2"></div>
                    <div className="h-3 bg-muted rounded w-1/2 mb-4"></div>
                    <div className="space-y-2">
                      <div className="h-3 bg-muted rounded"></div>
                      <div className="h-3 bg-muted rounded w-5/6"></div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : filteredEntries.length === 0 ? (
            <div className="text-center py-16" data-testid="empty-entries-state">
              <Globe className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-medium text-card-foreground mb-2">
                {searchTerm || filterType !== "all" ? "No entries found" : "No worldbuilding entries yet"}
              </h3>
              <p className="text-muted-foreground mb-4">
                {searchTerm || filterType !== "all"
                  ? "Try adjusting your search or filter."
                  : "Start building your world with locations, cultures, and systems."
                }
              </p>
              {!searchTerm && filterType === "all" && (
                <Button 
                  onClick={() => setShowEntryModal(true)}
                  data-testid="button-create-first-entry"
                >
                  <Plus className="mr-2 h-4 w-4" />
                  Create Your First Entry
                </Button>
              )}
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredEntries.map((entry: WorldbuildingEntry) => {
                const typeInfo = getTypeInfo(entry.type);
                const IconComponent = typeInfo.icon;

                return (
                  <Card key={entry.id} className="hover:shadow-lg transition-shadow" data-testid={`card-entry-${entry.id}`}>
                    <CardHeader className="pb-3">
                      <div className="flex items-start justify-between">
                        <div className="flex items-center space-x-3">
                          <div className="w-12 h-12 bg-accent/10 rounded-lg flex items-center justify-center">
                            <IconComponent className={`h-6 w-6 ${typeInfo.color}`} />
                          </div>
                          <div>
                            <CardTitle className="text-lg" data-testid={`text-entry-title-${entry.id}`}>
                              {entry.title}
                            </CardTitle>
                            <Badge variant="outline" className="mt-1">
                              {typeInfo.label}
                            </Badge>
                          </div>
                        </div>
                        <div className="flex items-center space-x-1">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleEdit(entry)}
                            data-testid={`button-edit-entry-${entry.id}`}
                          >
                            <Edit className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleDelete(entry)}
                            data-testid={`button-delete-entry-${entry.id}`}
                          >
                            <Trash2 className="h-4 w-4 text-destructive" />
                          </Button>
                        </div>
                      </div>
                    </CardHeader>
                    <CardContent className="space-y-3">
                      {entry.description && (
                        <div>
                          <p className="text-sm text-muted-foreground line-clamp-3">
                            {entry.description}
                          </p>
                        </div>
                      )}

                      {entry.details && typeof entry.details === 'object' && 'content' in entry.details && entry.details.content && (
                        <div>
                          <h4 className="text-sm font-medium text-card-foreground mb-1">Details</h4>
                          <p className="text-sm text-muted-foreground line-clamp-2">
                            {entry.details && typeof entry.details === 'object' && 'content' in entry.details && typeof entry.details.content === 'string' ? entry.details.content : ''}
                          </p>
                        </div>
                      )}

                      {entry.tags && entry.tags.length > 0 && (
                        <div className="flex flex-wrap gap-1">
                          {entry.tags.slice(0, 3).map((tag, index) => (
                            <Badge key={index} variant="secondary" className="text-xs">
                              {tag}
                            </Badge>
                          ))}
                          {entry.tags.length > 3 && (
                            <Badge variant="secondary" className="text-xs">
                              +{entry.tags.length - 3} more
                            </Badge>
                          )}
                        </div>
                      )}

                      <div className="pt-2 border-t border-border">
                        <div className="flex items-center justify-between text-xs text-muted-foreground">
                          <span>Created {entry.createdAt ? new Date(entry.createdAt).toLocaleDateString() : 'Unknown'}</span>
                          <span>Updated {entry.updatedAt ? new Date(entry.updatedAt).toLocaleDateString() : 'Unknown'}</span>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          )}
        </div>
      </main>

      {/* Entry Modal */}
      <Dialog open={showEntryModal} onOpenChange={handleCloseModal}>
        <DialogContent className="sm:max-w-lg max-h-[90vh] overflow-y-auto" data-testid="modal-worldbuilding-entry">
          <DialogHeader>
            <DialogTitle>
              {editingEntry ? "Edit World Entry" : "Create New World Entry"}
            </DialogTitle>
            <DialogDescription>
              {editingEntry ? "Update your worldbuilding entry." : "Add a new element to your world."}
            </DialogDescription>
          </DialogHeader>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="title">Title *</Label>
              <Input
                id="title"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                placeholder="Enter entry title"
                required
                data-testid="input-entry-title"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="type">Type *</Label>
              <Select value={formData.type} onValueChange={(value) => setFormData({ ...formData, type: value })}>
                <SelectTrigger data-testid="select-entry-type">
                  <SelectValue placeholder="Select entry type" />
                </SelectTrigger>
                <SelectContent>
                  {entryTypes.map((type) => (
                    <SelectItem key={type.value} value={type.value}>
                      {type.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                placeholder="Brief description of this world element..."
                className="resize-none h-20"
                data-testid="input-entry-description"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="details">Detailed Information</Label>
              <Textarea
                id="details"
                value={formData.details}
                onChange={(e) => setFormData({ ...formData, details: e.target.value })}
                placeholder="Detailed information, history, rules, or characteristics..."
                className="resize-none h-32"
                data-testid="input-entry-details"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="tags">Tags</Label>
              <Input
                id="tags"
                value={formData.tags}
                onChange={(e) => setFormData({ ...formData, tags: e.target.value })}
                placeholder="Enter tags separated by commas"
                data-testid="input-entry-tags"
              />
              <p className="text-xs text-muted-foreground">
                Use tags to organize and categorize your entries (e.g., "capital city, trade hub, ancient")
              </p>
            </div>

            <div className="flex items-center justify-end space-x-3 pt-4 border-t border-border">
              <Button 
                type="button" 
                variant="outline" 
                onClick={handleCloseModal}
                disabled={createEntryMutation.isPending || updateEntryMutation.isPending}
                data-testid="button-cancel-entry"
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                disabled={createEntryMutation.isPending || updateEntryMutation.isPending || !formData.title.trim() || !formData.type}
                data-testid="button-save-entry"
              >
                {createEntryMutation.isPending || updateEntryMutation.isPending
                  ? "Saving..." 
                  : editingEntry ? "Update Entry" : "Create Entry"
                }
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

