                    <div className="text-center p-4 bg-slate-50 dark:bg-slate-700/50 rounded-lg">
                      <div className="text-2xl font-bold text-slate-900 dark:text-slate-100">
                        {Math.round(analytics.productivity.totalWritingTime / 60)}h
                      </div>
