                </CardContent>
              </Card>

              {/* Progress Towards Goals */}
              <Card className="bg-white/80 dark:bg-slate-800/80 backdrop-blur">
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Target className="h-5 w-5 text-red-600" />
