import { useState, useEffect } from 'react';
import { ScrollArea } from "@/components/ui/scroll-area";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { 
  MessageSquare, 
  X, 
  Plus,
  CheckCircle,
  Clock,
  Reply,
  MoreVertical,
  Edit,
  Trash
} from "lucide-react";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { formatDistanceToNow } from "date-fns";
import CommentThread from './comment-thread';
import AddCommentDialog from './add-comment-dialog';
import type { DocumentComment as Comment, User } from '@shared/schema';

interface CommentSidebarProps {
  documentId: string;
  comments: Comment[];
  currentUser: User | null;
  userRole?: 'owner' | 'editor' | 'reviewer' | 'reader' | null;
  onClose?: () => void;
  onAddComment: (content: string, range?: { start: number; end: number }) => void;
  onUpdateComment: (commentId: string, content: string) => void;
  onDeleteComment: (commentId: string) => void;
  onResolveComment: (commentId: string) => void;
  onReplyToComment: (commentId: string, content: string) => void;
}

export default function CommentSidebar({
  documentId,
  comments,
  currentUser,
  userRole,
  onClose,
  onAddComment,
  onUpdateComment,
  onDeleteComment,
  onResolveComment,
  onReplyToComment,
}: CommentSidebarProps) {
  const [showAddDialog, setShowAddDialog] = useState(false);
  const [selectedComment, setSelectedComment] = useState<Comment | null>(null);
  const [filter, setFilter] = useState<'all' | 'open' | 'resolved'>('all');

  // Filter comments based on selected filter
  const filteredComments = comments.filter((comment) => {
    if (filter === 'all') return true;
    if (filter === 'open') return !comment.resolved;
    if (filter === 'resolved') return comment.resolved;
    return true;
  });

  // Group comments by thread (parent comments and their replies)
  const commentThreads = filteredComments.reduce((threads, comment) => {
    if (!comment.parentId) {
      threads[comment.id] = {
        parent: comment,
        replies: filteredComments.filter(c => c.parentId === comment.id)
      };
    }
    return threads;
  }, {} as Record<string, { parent: Comment; replies: Comment[] }>);

  const canComment = userRole && userRole !== 'reader';

  return (
    <div className="h-full flex flex-col bg-background border-l border-border" data-testid="comment-sidebar">
      {/* Header */}
      <div className="p-4 border-b border-border">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center space-x-2">
            <MessageSquare className="h-5 w-5" />
            <h3 className="font-semibold">Comments</h3>
            <Badge variant="secondary">{comments.length}</Badge>
          </div>
          {onClose && (
            <Button
              variant="ghost"
              size="sm"
              onClick={onClose}
              data-testid="button-close-comments"
            >
              <X className="h-4 w-4" />
            </Button>
          )}
        </div>

        {/* Filter Tabs */}
        <div className="flex space-x-1">
          <Button
            variant={filter === 'all' ? 'default' : 'ghost'}
            size="sm"
            onClick={() => setFilter('all')}
            data-testid="filter-all-comments"
          >
            All ({comments.length})
          </Button>
          <Button
            variant={filter === 'open' ? 'default' : 'ghost'}
            size="sm"
            onClick={() => setFilter('open')}
            data-testid="filter-open-comments"
          >
            <Clock className="h-4 w-4 mr-1" />
            Open ({comments.filter(c => !c.resolved).length})
          </Button>
          <Button
            variant={filter === 'resolved' ? 'default' : 'ghost'}
            size="sm"
            onClick={() => setFilter('resolved')}
            data-testid="filter-resolved-comments"
          >
            <CheckCircle className="h-4 w-4 mr-1" />
            Resolved ({comments.filter(c => c.resolved).length})
          </Button>
        </div>
      </div>

      {/* Comments List */}
      <ScrollArea className="flex-1 p-4">
        {Object.keys(commentThreads).length === 0 ? (
          <div className="text-center py-8 text-muted-foreground">
            <MessageSquare className="h-12 w-12 mx-auto mb-3 opacity-50" />
            <p>No comments yet</p>
            {canComment && (
              <p className="text-sm mt-2">Select text and click "Add Comment" to start</p>
            )}
          </div>
        ) : (
          <div className="space-y-4">
            {Object.entries(commentThreads).map(([threadId, thread]) => (
              <CommentThread
                key={threadId}
                comment={thread.parent}
                replies={thread.replies}
                currentUser={currentUser}
                userRole={userRole}
                onReply={(content) => onReplyToComment(thread.parent.id, content)}
                onEdit={(content) => onUpdateComment(thread.parent.id, content)}
                onDelete={() => onDeleteComment(thread.parent.id)}
                onResolve={() => onResolveComment(thread.parent.id)}
              />
            ))}
          </div>
        )}
      </ScrollArea>

      {/* Add Comment Button */}
      {canComment && (
        <div className="p-4 border-t border-border">
          <Button
            className="w-full"
            onClick={() => setShowAddDialog(true)}
            data-testid="button-add-new-comment"
          >
            <Plus className="h-4 w-4 mr-2" />
            Add Comment
          </Button>
        </div>
      )}

      {/* Add Comment Dialog */}
      {showAddDialog && (
        <AddCommentDialog
          open={showAddDialog}
          onClose={() => setShowAddDialog(false)}
          onSubmit={(content, range) => {
            onAddComment(content, range);
            setShowAddDialog(false);
          }}
        />
      )}
    </div>
  );
}

client/src/components/comment-thread.tsx.content:
import { useState } from 'react';
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Textarea } from "@/components/ui/textarea";
import { 
  Reply,
  Edit,
  Trash,
  CheckCircle,
  Clock,
  MoreVertical,
  Send,
  X,
  Check
} from "lucide-react";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { formatDistanceToNow } from "date-fns";
import type { DocumentComment as Comment, User } from '@shared/schema';

interface CommentThreadProps {
  comment: Comment;
  replies: Comment[];
  currentUser: User | null;
  userRole?: 'owner' | 'editor' | 'reviewer' | 'reader' | null;
  onReply: (content: string) => void;
  onEdit: (content: string) => void;
  onDelete: () => void;
  onResolve: () => void;
}

export default function CommentThread({
  comment,
  replies,
  currentUser,
  userRole,
  onReply,
  onEdit,
  onDelete,
  onResolve,
}: CommentThreadProps) {
  const [isReplying, setIsReplying] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [replyContent, setReplyContent] = useState('');
  const [editContent, setEditContent] = useState(comment.content || '');

  const isAuthor = currentUser?.id === comment.authorId;
  const canEdit = isAuthor && userRole !== 'reader';
  const canDelete = (isAuthor || userRole === 'owner') && userRole !== 'reader';
  const canResolve = userRole !== 'reader';
  const canReply = userRole !== 'reader';

  const handleReply = () => {
    if (replyContent.trim()) {
      onReply(replyContent.trim());
      setReplyContent('');
      setIsReplying(false);
    }
  };

  const handleEdit = () => {
    if (editContent.trim() && editContent !== comment.content) {
      onEdit(editContent.trim());
      setIsEditing(false);
    }
  };

  const getUserInitials = (user: any) => {
    if (user?.firstName && user?.lastName) {
      return `${user.firstName[0]}${user.lastName[0]}`.toUpperCase();
    }
    if (user?.email) {
      return user.email[0].toUpperCase();
    }
    return 'U';
  };

  return (
    <Card className="p-4" data-testid={`comment-thread-${comment.id}`}>
      {/* Main Comment */}
      <div className="space-y-3">
        <div className="flex items-start justify-between">
          <div className="flex items-start space-x-3">
            <Avatar className="h-8 w-8">
              {comment.author?.profileImageUrl && (
                <AvatarImage src={comment.author.profileImageUrl} />
              )}
              <AvatarFallback>{getUserInitials(comment.author)}</AvatarFallback>
            </Avatar>
            <div className="flex-1">
              <div className="flex items-center space-x-2">
                <span className="font-medium text-sm">
                  {comment.author?.firstName} {comment.author?.lastName}
                </span>
                <span className="text-xs text-muted-foreground">
                  {comment.createdAt && formatDistanceToNow(new Date(comment.createdAt), { addSuffix: true })}
                </span>
                {comment.resolved && (
                  <Badge variant="secondary" className="text-xs">
                    <CheckCircle className="h-3 w-3 mr-1" />
                    Resolved
                  </Badge>
                )}
              </div>

              {/* Comment Content or Edit Mode */}
              {isEditing ? (
                <div className="mt-2 space-y-2">
                  <Textarea
                    value={editContent}
                    onChange={(e) => setEditContent(e.target.value)}
                    className="min-h-[60px]"
                    data-testid="edit-comment-textarea"
                    autoFocus
                  />
                  <div className="flex space-x-2">
                    <Button
                      size="sm"
                      onClick={handleEdit}
                      data-testid="button-save-edit"
                    >
                      <Check className="h-4 w-4 mr-1" />
                      Save
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => {
                        setIsEditing(false);
                        setEditContent(comment.content || '');
                      }}
                      data-testid="button-cancel-edit"
                    >
                      Cancel
                    </Button>
                  </div>
                </div>
              ) : (
                <p className="text-sm mt-1 whitespace-pre-wrap">{comment.content}</p>
              )}

              {/* Text Range Reference */}
              {comment.range && (
                <div className="mt-2 p-2 bg-muted rounded text-xs">
                  <span className="text-muted-foreground">Reference: </span>
                  <span>Characters {comment.range.start} - {comment.range.end}</span>
                </div>
              )}
            </div>
          </div>

          {/* Actions Menu */}
          {!isEditing && (canEdit || canDelete || canResolve) && (
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" size="sm" data-testid={`comment-menu-${comment.id}`}>
                  <MoreVertical className="h-4 w-4" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                {canEdit && !comment.resolved && (
                  <DropdownMenuItem
                    onClick={() => setIsEditing(true)}
                    data-testid="menu-edit-comment"
                  >
                    <Edit className="h-4 w-4 mr-2" />
                    Edit
                  </DropdownMenuItem>
                )}
                {canResolve && !comment.resolved && (
                  <DropdownMenuItem
                    onClick={onResolve}
                    data-testid="menu-resolve-comment"
                  >
                    <CheckCircle className="h-4 w-4 mr-2" />
                    Resolve
                  </DropdownMenuItem>
                )}
                {canDelete && (
                  <DropdownMenuItem
                    onClick={onDelete}
                    className="text-destructive"
                    data-testid="menu-delete-comment"
                  >
                    <Trash className="h-4 w-4 mr-2" />
                    Delete
                  </DropdownMenuItem>
                )}
              </DropdownMenuContent>
            </DropdownMenu>
          )}
        </div>

        {/* Reply Button */}
        {!isEditing && canReply && !comment.resolved && (
          <div className="flex items-center space-x-2">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setIsReplying(true)}
              data-testid="button-reply"
            >
              <Reply className="h-4 w-4 mr-1" />
              Reply
            </Button>
          </div>
        )}

        {/* Reply Form */}
        {isReplying && (
          <div className="ml-11 space-y-2">
            <Textarea
              value={replyContent}
              onChange={(e) => setReplyContent(e.target.value)}
              placeholder="Write a reply..."
              className="min-h-[60px]"
              data-testid="reply-textarea"
              autoFocus
            />
            <div className="flex space-x-2">
              <Button
                size="sm"
                onClick={handleReply}
                disabled={!replyContent.trim()}
                data-testid="button-send-reply"
              >
                <Send className="h-4 w-4 mr-1" />
                Send
              </Button>
              <Button
                size="sm"
                variant="outline"
                onClick={() => {
                  setIsReplying(false);
                  setReplyContent('');
                }}
                data-testid="button-cancel-reply"
              >
                Cancel
              </Button>
            </div>
          </div>
        )}

        {/* Replies */}
        {replies.length > 0 && (
          <div className="ml-11 space-y-3">
            {replies.map((reply) => (
              <div key={reply.id} className="flex items-start space-x-3">
                <Avatar className="h-7 w-7">
                  {reply.author?.profileImageUrl && (
                    <AvatarImage src={reply.author.profileImageUrl} />
                  )}
                  <AvatarFallback className="text-xs">
                    {getUserInitials(reply.author)}
                  </AvatarFallback>
                </Avatar>
                <div className="flex-1">
                  <div className="flex items-center space-x-2">
                    <span className="font-medium text-sm">
                      {reply.author?.firstName} {reply.author?.lastName}
                    </span>
                    <span className="text-xs text-muted-foreground">
                      {reply.createdAt && formatDistanceToNow(new Date(reply.createdAt), { addSuffix: true })}
                    </span>
                  </div>
                  <p className="text-sm mt-1 whitespace-pre-wrap">{reply.content}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </Card>
  );
}

client/src/components/presence-indicator.content:
import { useMemo } from 'react';
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import {
  HoverCard,
  HoverCardContent,
  HoverCardTrigger,
} from "@/components/ui/hover-card";
import { 
  Users, 
  Circle, 
  Edit3, 
  Eye, 
  MessageCircle,
  UserCheck
} from "lucide-react";
import type { User, CollaborationPresence } from '@shared/schema';

interface PresenceIndicatorProps {
  onlineUsers: Map<number, any>;
  currentUser?: User | null;
  className?: string;
}

export default function PresenceIndicator({
  onlineUsers,
  currentUser,
  className = ""
}: PresenceIndicatorProps) {
  // Filter out current user and format presence data
  const otherUsers = useMemo(() => {
    const users: any[] = [];
    onlineUsers.forEach((state, clientId) => {
      if (state?.user && state.user.id !== currentUser?.id) {
        users.push({
          ...state.user,
          clientId,
          cursor: state.cursor,
        });
      }
    });
    return users;
  }, [onlineUsers, currentUser]);

  const getUserInitials = (user: any) => {
    if (user?.name) {
      const parts = user.name.split(' ');
      if (parts.length >= 2) {
        return `${parts[0][0]}${parts[1][0]}`.toUpperCase();
      }
      return user.name[0].toUpperCase();
    }
    return 'U';
  };

  const getRoleBadgeColor = (role: string) => {
    switch (role) {
      case 'owner':
        return 'default';
      case 'editor':
        return 'secondary';
      case 'reviewer':
        return 'outline';
      case 'reader':
        return 'ghost';
      default:
        return 'secondary';
    }
  };

  const getRoleIcon = (role: string) => {
    switch (role) {
      case 'owner':
        return <UserCheck className="h-3 w-3" />;
      case 'editor':
        return <Edit3 className="h-3 w-3" />;
      case 'reviewer':
        return <MessageCircle className="h-3 w-3" />;
      case 'reader':
        return <Eye className="h-3 w-3" />;
      default:
        return null;
    }
  };

  if (otherUsers.length === 0) {
    return (
      <div className={`flex items-center space-x-2 text-muted-foreground ${className}`} data-testid="presence-indicator-empty">
        <Users className="h-4 w-4" />
        <span className="text-sm">No one else online</span>
      </div>
    );
  }

  return (
    <div className={`flex items-center space-x-3 ${className}`} data-testid="presence-indicator">
      <div className="flex items-center space-x-2">
        <Users className="h-4 w-4 text-muted-foreground" />
        <span className="text-sm font-medium">
          {otherUsers.length} {otherUsers.length === 1 ? 'person' : 'people'} online
        </span>
      </div>

      {/* Avatar Stack */}
      <div className="flex -space-x-2">
        {otherUsers.slice(0, 5).map((user, index) => (
          <HoverCard key={`user-${user.clientId || index}`}>
            <HoverCardTrigger>
              <div 
                className="relative"
                data-testid={`presence-avatar-${user.id}`}
              >
                <Avatar 
                  className="h-8 w-8 border-2 border-background"
                  style={{
                    borderColor: user.color || '#808080',
                    boxShadow: `0 0 0 2px ${user.color || '#808080'}20`
                  }}
                >
                  {user.profileImageUrl && (
                    <AvatarImage src={user.profileImageUrl} />
                  )}
                  <AvatarFallback
                    style={{ 
                      backgroundColor: `${user.color || '#808080'}20`,
                      color: user.color || '#808080'
                    }}
                  >
                    {getUserInitials(user)}
                  </AvatarFallback>
                </Avatar>

                {/* Online Status Indicator */}
                <div className="absolute -bottom-0.5 -right-0.5">
                  <Circle 
                    className="h-3 w-3 fill-green-500 text-green-500"
                    data-testid={`status-indicator-${user.id}`}
                  />
                </div>
              </div>
            </HoverCardTrigger>

            <HoverCardContent className="w-64" align="center">
              <div className="space-y-2">
                <div className="flex items-start justify-between">
                  <div className="flex items-center space-x-2">
                    <Avatar className="h-10 w-10">
                      {user.profileImageUrl && (
                        <AvatarImage src={user.profileImageUrl} />
                      )}
                      <AvatarFallback>{getUserInitials(user)}</AvatarFallback>
                    </Avatar>
                    <div>
                      <p className="font-medium text-sm">{user.name}</p>
                      <div className="flex items-center space-x-1 mt-1">
                        <Circle className="h-2 w-2 fill-green-500 text-green-500" />
                        <span className="text-xs text-muted-foreground">Active now</span>
                      </div>
                    </div>
                  </div>
                </div>

                {user.role && (
                  <Badge 
                    variant={getRoleBadgeColor(user.role) as any}
                    className="capitalize"
                  >
                    {getRoleIcon(user.role)}
                    <span className="ml-1">{user.role}</span>
                  </Badge>
                )}

                {user.cursor && (
                  <div className="text-xs text-muted-foreground">
                    <span>Editing at position {user.cursor.from}</span>
                  </div>
                )}
              </div>
            </HoverCardContent>
          </HoverCard>
        ))}

        {otherUsers.length > 5 && (
          <div 
            className="flex h-8 w-8 items-center justify-center rounded-full bg-muted border-2 border-background"
            data-testid="presence-overflow-count"
          >
            <span className="text-xs font-medium">+{otherUsers.length - 5}</span>
          </div>
        )}
      </div>
    </div>
  );
}

