import { useState, useEffect, useRef } from "react";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Input } from "@/components/ui/input";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandItem,
  CommandList,
} from "@/components/ui/command";
import { 
  Search, 
  FileText, 
  Users, 
  Globe, 
  Clock,
  ArrowRight
} from "lucide-react";
import { SearchResult } from "@shared/schema";

export function SearchBar() {
  const [open, setOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [debouncedQuery, setDebouncedQuery] = useState("");
  const [, navigate] = useLocation();
  const inputRef = useRef<HTMLInputElement>(null);

  // Debounce search query
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedQuery(searchQuery);
    }, 300);

    return () => clearTimeout(timer);
  }, [searchQuery]);

  // Fetch search results
  const { data: results = [], isLoading } = useQuery<SearchResult[]>({
    queryKey: [`/api/search?q=${debouncedQuery}&limit=5`],
    enabled: debouncedQuery.length >= 2,
  });

  // Get icon based on result type
  const getIcon = (type: string) => {
    switch (type) {
      case "document":
        return <FileText className="h-4 w-4" />;
      case "character":
        return <Users className="h-4 w-4" />;
      case "worldbuilding":
        return <Globe className="h-4 w-4" />;
      case "timeline":
        return <Clock className="h-4 w-4" />;
      default:
        return <FileText className="h-4 w-4" />;
    }
  };

  // Navigate to result
  const handleSelect = (result: SearchResult) => {
    setOpen(false);
    setSearchQuery("");

    // Navigate based on result type
    switch (result.type) {
      case "document":
        navigate(`/projects/${result.projectId}/documents/${result.id}`);
        break;
      case "character":
        navigate(`/projects/${result.projectId}?tab=characters`);
        break;
      case "worldbuilding":
        navigate(`/projects/${result.projectId}?tab=worldbuilding`);
        break;
      case "timeline":
        navigate(`/projects/${result.projectId}?tab=timeline`);
        break;
    }
  };

  // Navigate to full search results
  const handleViewAll = () => {
    setOpen(false);
    navigate(`/search?q=${encodeURIComponent(searchQuery)}`);
  };

  // Keyboard shortcuts (Cmd/Ctrl+K)
  useEffect(() => {
    const down = (e: KeyboardEvent) => {
      if (e.key === "k" && (e.metaKey || e.ctrlKey)) {
        e.preventDefault();
        inputRef.current?.focus();
      }
    };

    document.addEventListener("keydown", down);
    return () => document.removeEventListener("keydown", down);
  }, []);

  return (
    <div className="relative w-full max-w-sm" data-testid="search-bar-container">
      <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
      <Input
        ref={inputRef}
        type="text"
        placeholder="Search... (âŒ˜K)"
        value={searchQuery}
        onChange={(e) => {
          setSearchQuery(e.target.value);
          setOpen(e.target.value.length >= 2);
        }}
        onFocus={() => searchQuery.length >= 2 && setOpen(true)}
        className="pl-9 pr-4"
        data-testid="input-search"
      />

      {open && searchQuery.length >= 2 && (
        <div className="absolute top-full left-0 right-0 mt-2 z-50 bg-popover border rounded-md shadow-lg">
          <Command>
            <CommandList>
              {isLoading && (
                <CommandEmpty>Searching...</CommandEmpty>
              )}
              {!isLoading && results.length === 0 && (
                <CommandEmpty data-testid="text-no-results">No results found</CommandEmpty>
              )}
              {!isLoading && results.length > 0 && (
                <>
                  <CommandGroup heading="Quick Results">
                    {results.map((result) => (
                      <CommandItem
                        key={`${result.type}-${result.id}`}
                        onSelect={() => handleSelect(result)}
                        className="cursor-pointer"
                        data-testid={`search-result-${result.id}`}
                      >
                        <div className="flex items-center gap-3 w-full">
                          {getIcon(result.type)}
                          <div className="flex-1 overflow-hidden">
                            <div className="font-medium truncate">{result.title}</div>
                            <div className="text-xs text-muted-foreground truncate">
                              {result.projectTitle}
                            </div>
                          </div>
                          <span className="text-xs text-muted-foreground capitalize">
                            {result.type}
                          </span>
                        </div>
                      </CommandItem>
                    ))}
                  </CommandGroup>
                  <div className="border-t">
                    <CommandItem
                      onSelect={handleViewAll}
                      className="cursor-pointer justify-center text-sm text-primary"
                      data-testid="button-view-all-results"
                    >
                      <span>View all results</span>
                      <ArrowRight className="ml-2 h-4 w-4" />
                    </CommandItem>
                  </div>
                </>
              )}
            </CommandList>
          </Command>
        </div>
      )}
    </div>
  );
}

client/src/pages/search-results.tsx.content:
import { useEffect, useState } from "react";
import { useLocation, Link } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { 
  Search, 
  FileText, 
  Users, 
  Globe, 
  Clock,
  ChevronRight
} from "lucide-react";
import { SearchResult } from "@shared/schema";

export default function SearchResultsPage() {
  const [location, navigate] = useLocation();
  const searchParams = new URLSearchParams(location.split('?')[1] || '');
  const initialQuery = searchParams.get('q') || '';

  const [searchQuery, setSearchQuery] = useState(initialQuery);
  const [debouncedQuery, setDebouncedQuery] = useState(initialQuery);

  // Debounce search query
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedQuery(searchQuery);
      // Update URL
      if (searchQuery) {
        navigate(`/search?q=${encodeURIComponent(searchQuery)}`);
      }
    }, 300);

    return () => clearTimeout(timer);
  }, [searchQuery, navigate]);

  // Fetch search results
  const { data: results = [], isLoading } = useQuery<SearchResult[]>({
    queryKey: [`/api/search?q=${debouncedQuery}&limit=100`],
    enabled: debouncedQuery.length >= 2,
  });

  // Categorize results
  const documents = results.filter(r => r.type === 'document');
  const characters = results.filter(r => r.type === 'character');
  const worldbuilding = results.filter(r => r.type === 'worldbuilding');
  const timeline = results.filter(r => r.type === 'timeline');

  const allCount = results.length;
  const counts = {
    documents: documents.length,
    characters: characters.length,
    worldbuilding: worldbuilding.length,
    timeline: timeline.length,
  };

  // Get navigation path for result
  const getResultPath = (result: SearchResult) => {
    switch (result.type) {
      case "document":
        return `/projects/${result.projectId}/documents/${result.id}`;
      case "character":
        return `/projects/${result.projectId}?tab=characters`;
      case "worldbuilding":
        return `/projects/${result.projectId}?tab=worldbuilding`;
      case "timeline":
        return `/projects/${result.projectId}?tab=timeline`;
      default:
        return `/projects/${result.projectId}`;
    }
  };

  // Truncate content for preview
  const truncateContent = (content: string, maxLength: number = 150) => {
    const stripped = content.replace(/<[^>]*>/g, '').replace(/&[^;]+;/g, ' ').trim();
    if (stripped.length <= maxLength) return stripped;
    return stripped.substring(0, maxLength).trim() + '...';
  };

  // Result card component
  const ResultCard = ({ result }: { result: SearchResult }) => (
    <Link href={getResultPath(result)}>
      <Card className="cursor-pointer hover:bg-accent/50 transition-colors" data-testid={`search-result-card-${result.id}`}>
        <CardHeader className="pb-3">
          <div className="flex items-start justify-between">
            <div className="flex items-start gap-3 flex-1">
              <div className="mt-1">
                {result.type === 'document' && <FileText className="h-5 w-5 text-blue-500" />}
                {result.type === 'character' && <Users className="h-5 w-5 text-purple-500" />}
                {result.type === 'worldbuilding' && <Globe className="h-5 w-5 text-green-500" />}
                {result.type === 'timeline' && <Clock className="h-5 w-5 text-orange-500" />}
              </div>
              <div className="flex-1">
                <CardTitle className="text-base">{result.title}</CardTitle>
                <CardDescription className="mt-1">
                  {result.projectTitle}
                </CardDescription>
              </div>
            </div>
            <Badge variant="outline" className="capitalize">
              {result.type}
            </Badge>
          </div>
        </CardHeader>
        {result.content && (
          <CardContent className="pt-0">
            <p className="text-sm text-muted-foreground line-clamp-2">
              {truncateContent(result.content)}
            </p>
          </CardContent>
        )}
      </Card>
    </Link>
  );

  return (
    <div className="container mx-auto p-6 max-w-6xl">
      <div className="mb-6">
        <h1 className="text-3xl font-bold mb-4">Search</h1>
        <div className="relative">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <Input
            type="text"
            placeholder="Search across all your projects..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-9"
            autoFocus
            data-testid="input-search-page"
          />
        </div>
      </div>

      {debouncedQuery.length < 2 ? (
        <Card>
          <CardContent className="py-12 text-center text-muted-foreground">
            <Search className="h-12 w-12 mx-auto mb-4 opacity-50" />
            <p>Enter at least 2 characters to search</p>
          </CardContent>
        </Card>
      ) : isLoading ? (
        <Card>
          <CardContent className="py-12 text-center text-muted-foreground">
            <p>Searching...</p>
          </CardContent>
        </Card>
      ) : results.length === 0 ? (
        <Card>
          <CardContent className="py-12 text-center text-muted-foreground" data-testid="text-no-search-results">
